+/**
+ * @brief: make decision
+ */
+void FrontVehicle::MakeDecisions(Frame* frame,
+                             ReferenceLineInfo* const reference_line_info) {
+  CHECK_NOTNULL(frame);
+  CHECK_NOTNULL(reference_line_info);
+
+  MakeSidePassDecision(reference_line_info);
+
+  MakeStopDecision(reference_line_info);
