+  i += 32;
+  eph->cuc = getbits(buff, i, 16) * P2_29;
+  i += 16;
+  eph->e = getbitu(buff, i, 32) * P2_33;
+  i += 32;
+  eph->cus = getbits(buff, i, 16) * P2_29;
+  i += 16;
+  sqrtA = getbitu(buff, i, 32) * P2_19;
+  i += 32;
+  eph->toes = getbitu(buff, i, 16) * 16.0;
+  i += 16;
+  eph->fit = getbitu(buff, i, 1) ? 0.0 : 4.0; /* 0:4hr,1:>4hr */
+
+  eph->A = sqrtA * sqrtA;
+
+  return 2;
 }
