-static double det(double m00, double m01, double m02, double m10, double m11,
-                  double m12, double m20, double m21, double m22) {
-  return m00 * m11 * m22 + m01 * m12 * m20 + m02 * m10 * m21 - m20 * m11 * m02 -
-         m10 * m01 * m22 - m00 * m21 * m12;
