+void VectorMap::subscribe(ros::NodeHandle& nh, category_t category, const size_t max_retries)
+{
+  size_t tries = 0;
+  registerSubscriber(nh, category);
+  ros::Rate rate(1);
+  while (ros::ok() && !hasSubscribed(category) && tries++ < max_retries)
+  {
+    ros::spinOnce();
+    rate.sleep();
+  }
+}
