+const std::vector<double>
+WaypointFilter::calcVelParamFromVmax(const double vmax) const {
+  std::vector<double> param(2, 0.0);
+  param[0] = (vmax - velocity_min_) / (r_th_ - r_min_); // bias
+  param[1] = vmax - param[0] * r_th_;                   // vel_intersept
