-            (*pointsOut)[index].y = points[indices[i]].y;
-            (*oppositePointsOut)[index].x = oppositePoints[indices[i]].x;
-            (*oppositePointsOut)[index].y = oppositePoints[indices[i]].y;
-            (*scoreOut)[index] = score[indices[i]];
-            index++;
-        }
-
-    }
