+bool MappingHelpers::GetPointFromDataList(UtilityHNS::AisanPointsFileReader* pPointsData,const int& pid, WayPoint& out_wp)
+{
+	AisanPointsFileReader::AisanPoints* pP =  pPointsData->GetDataRowById(pid);
+
+	if(pP!=nullptr)
+	{
+		out_wp.id = pP->PID;
+		out_wp.pos.x = pP->Ly;
+		out_wp.pos.y = pP->Bx;
+		out_wp.pos.z = pP->H;
+		return true;
+	}
+
+	return false;
+}
