+	UtilityHNS::AisanPointsFileReader::AisanPoints* pP = nullptr;
+	UtilityHNS::AisanNodesFileReader::AisanNode* pN = nullptr;
+
+	pL = pLaneData->GetDataRowById(LnID);
+	if(pL!=nullptr)
+		return pL->BNID;
+
+	return 0;
