-		bool bMakeNewPlan = false;
-
-		if(m_SimParams.mapSource == MAP_KML_FILE && !m_bMap)
-		{
-			m_bMap = true;
-			PlannerHNS::MappingHelpers::LoadKML(m_SimParams.KmlMapPath, m_Map);
-			if(!m_SimParams.bRandomStart)
-				InitializeSimuCar(m_SimParams.startPose);
